import { Text,Stack,Flex, Heading,Image } from "@chakra-ui/react";
import timeIcon from "./icon/time.svg";
import bombIcon from "./icon/bomb.svg";

function App() {
  return (
    <Stack minH="100vh" textAlign="center" bgColor="#8BA4B3" fontWeight="bold">
      <Heading as="h1" size="xl" color="white" mt="17vh" mb="8vh">
        Minesweeper
      </Heading>
      <Flex justifyContent="center" fontSize="20px">
        <Flex w="210px" h="70px" bgColor="#FCF7EC" borderRadius="20" justifyContent="space-evenly" alignItems="center" mr="50px" padding="10px">
          <Image src={timeIcon} h="45px" w="45px"/>
          <Text>Timer</Text>
          <Text>500 s</Text>
        </Flex>
        <Flex w="210px" h="70px" bgColor="#FCF7EC" borderRadius="20" justifyContent="space-evenly" alignItems="center" ml="50px" padding="10px">
          <Image src={bombIcon} w="50px" h="45px"/>
          <Text>Bombs</Text>
          <Text>20</Text>
        </Flex>
      </Flex>

    </Stack>

  );
}

export default App;
