import { Text,Stack,Flex, Heading, Box,Image } from "@chakra-ui/react";
import timeIcon from "./icon/time.svg";
import bombIcon from "./icon/bomb.svg";

function App() {
  return (
    <Stack minH="100vh" textAlign="center" bgColor="#8BA4B3">
      <Heading as="h1" size="xl" color="white" fontWeight="bold" mt="17vh">
        Minesweeper
      </Heading>
      <Flex justifyContent="center">
        <Flex w="210px" h="70px" bgColor="#FCF7EC" borderRadius="20">
          <Image src={timeIcon} h="45px" w="45px"/>
          <Text>Timer</Text>
          <Text>59 s</Text>
        </Flex>
        <Box w="210px" h="70px" bgColor="#FCF7EC" borderRadius="20">
          <Image src={bombIcon} w="50px" h="45px"/>
          <Text>Bombs</Text>
          <Text>20</Text>
        </Box>
      </Flex>

    </Stack>

  );
}

export default App;
